import {
  Component,
  HostBinding,
  OnInit
} from '@angular/core';
import {Title} from '@angular/platform-browser';
import {DeviceService} from 'ng-parts/ng-parts';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.less']
})
export class AppComponent implements OnInit {
  @HostBinding('attr.class') deviceFamily: string = this.deviceService.Device.family;

  title = 'app works!';

  constructor (private deviceService: DeviceService, private titleService: Title) {}

  ngOnInit () {
    this.titleService.setTitle(this.title);

    this.deviceService.device$.subscribe(
      device => this.deviceFamily = device.family
    );
  }
}
